import sqlite3, pandas as pd, numpy as np; conn = sqlite3.connect('data/sales.db'); dates = pd.date_range(start=pd.Timestamp.now() - pd.Timedelta(days=365), end=pd.Timestamp.now()); sales = np.random.normal(1000, 200, len(dates)); df = pd.DataFrame({'date': dates.strftime('%Y-%m-%d'), 'amount': sales}); df.to_sql('sales_data', conn, if_exists='replace', index=False); conn.close(); print('Sample data added!')
